/*slider*/
.banner-slider{
    margin-bottom:70px;
}

.banner-slider .carousel-inner .carousel-item {
    position: relative;
}

.banner-slider .carousel-inner .carousel-item .carousel-caption {
  width: 75%;
  bottom: 50%;
  right: 50%;
  left: unset;
  transform: translate(50%, 50%);
  /*display: flex;*/
  flex-direction: column-reverse;
}

.banner-slider .carousel-inner .carousel-item .carousel-caption h3 {
    font-size: 40px;
    color: #fff;
    margin-bottom:20px;
}
.banner-slider .carousel-inner .carousel-item .carousel-caption p {
    font-size: 20px;
}
@media (max-width: 620px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption {
    width: 75%;
  }
}
.banner-slider .carousel-inner .carousel-item .carousel-caption h3 {
  font-size: 55px;
  text-transform:capitalize;
}
@media screen and (max-width: 1199px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption h3 {
    font-size: 45px;
  }
}
@media screen and (max-width: 991px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption h3 {
    font-size: 30px;
  }
}
@media (max-width: 575px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption h3 {
    margin-bottom: 0;
    font-size: 18px;
    margin-bottom:15px;
  }
}
@media (max-width: 320px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption h3 {
    margin-bottom: 0;
    font-size: 16px;
  }
}
.banner-slider .carousel-inner .carousel-item .carousel-caption p {
  font-size: 25px;
  text-transform:capitalize;
}
@media screen and (max-width: 1199px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption p {
    font-size: 20px;
  }
}
@media screen and (max-width: 991px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption p {
    font-size: 18px;
    line-height: 20px;
  }
}
@media (max-width: 575px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption p {
    font-size: 14px;
    line-height: 15px;
  }
}
@media (max-width: 320px) {
  .banner-slider .carousel-inner .carousel-item .carousel-caption p {
    font-size: 10px;
    line-height: 15px;
  }
}

.banner-slider .carousel-inner .carousel-item:after {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0,0,0,.6);
    content: "";
}

@media (max-width:395px)
{
    .banner-slider .carousel-inner .carousel-item .carousel-caption h3{
        /*margin-top:-7px;*/
        font-size: 16px;
    }
    .banner-slider .carousel-inner .carousel-item .carousel-caption p{
        /*margin-top:-7px;*/
        font-size: 12px;
    }
}



