.bottom-shape {
    display: flex;
    margin: 20px 0 30px;
}
.bottom-shape .left-shape {
    display: flex;
    flex-wrap: wrap;
    height: 23px;
    width: 40px;
}
.left-shape {
    position: relative;
}

.bottom-shape .left-shape:before {
    width: 20px;
    background-color: #e6992a;
    left: 20px;
}
.left-shape:before {
    position: absolute;
    display: inline-block;
    width: 80%;
    background-color: #161616;
    height: 2px;
    top: 0;
    bottom: 0;
    margin-top: auto;
    margin-bottom: auto;
    left: 10px;
    margin-bottom: 14px;
    width: 20px;
    content: "";
}

.bottom-shape .left-shape:after {
    width: 40px;
    background-color: #e6992a;
}
.left-shape:after {
    margin-top: 15px;
    width: 30px;
    left: 0;
}
.left-shape:before {
    position: absolute;
    display: inline-block;
    width: 80%;
    background-color: #161616;
    height: 2px;
    top: 0;
    bottom: 0;
    margin-top: auto;
    margin-bottom: auto;
    left: 10px;
    margin-bottom: 14px;
    width: 20px;
    content: "";
}
.bottom-shape .left-shape:nth-child(2) {
    margin-left: 10px;
}
.bottom-shape .left-shape {
    display: flex;
    flex-wrap: wrap;
    height: 23px;
    width: 40px;
}

.bottom-shape .left-shape:nth-child(2):before {
    left: 0;
}
.bottom-shape .left-shape:before {
    width: 20px;
    background-color: #e6992a;
    left: 20px;
}
.left-shape:after {
    position: absolute;
    display: inline-block;
    width: 80%;
    background-color: #161616;
    height: 2px;
    top: 0;
    bottom: 0;
    margin-top: auto;
    margin-bottom: auto;
    left: 10px;
    margin-bottom: 14px;
    width: 20px;
    content: "";
}
.bottom-shape .left-shape:after {
    width: 40px;
    background-color: #e6992a;
}

.left-shape:after {
    margin-top: 15px;
    width: 30px;
    left: 0;
}
