/*Quick support*/
.quick_support {
  padding: 50px 0;
  position: relative;
  background-repeat: repeat;
  background-color: #1b1b1a;
  color: black;
}
.quick_support .support-wrap {
    z-index: 9;
}
.quick_support .btn {
  border-radius: 0px !important;
  background: #E6992A;
  text-transform: uppercase;
  color: #fff;
  line-height: 0;
  font-size: 18px !important;
  cursor: pointer;
  transition: color 0.4s linear;
  padding: 15px 40px !important;
  font-weight: bold !important;
  position: relative;
  z-index: 1;
  border: 0px !important;
  overflow: hidden;
  margin: 0;
}
.quick_support .btn::before {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  width: 101%;
  height: 101%;
  background: #f26e21;
  z-index: 1;
  border-radius: 0px;
  transition: transform 0.5s;
  transition-timing-function: ease;
  transform-origin: 0 0;
  transition-timing-function: cubic-bezier(0.5, 1.6, 0.4, 0.7);
  transform: scaleX(0);
  border-radius: 0;
}
.quick_support .btn:hover::before {
  transform: scaleX(1);
  color: #fff !important;
  z-index: -1;
}

@media (max-width: 1600px){
.quick_support .support-wrap {
    margin-left: 0;
    padding-left: 0;
}
}
@media (max-width:1169px){
    .quick_support .btn{
        padding: 15px 17px !important;
    }
}
