.bg-grey {
    background-color: #f3f3f3;
    padding: 80px 0;
}
.title-text {
    text-align: center;
    position: relative;
}
.title-text span {
    font-size: 20px;
    color: #888;
}

.title-text h2 {
    font-size: 35px;
    font-weight: 700;
    padding-bottom: 70px;
    margin-bottom: 0;
    margin-top:0;
    text-transform:capitalize;
}

.flow-steps .each-step {
	position: relative;
	width: 25%
}

@media(max-width:768px) {
	.flow-steps .each-step {
		width: 100%
	}
}

.flow-steps .each-step img {
	margin-bottom: 30px
}

.flow-steps .each-step .icon {
	position: relative;
	justify-content: center;
	text-align: center
}

@media(max-width:768px) {
	.flow-steps .each-step .icon {
		z-index: 90;
		position: relative;
	}
}

.flow-steps .each-step .icon:after {
    position: absolute;
    border-bottom: 2px dashed;
    background-repeat: repeat;
    width: 100%;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    margin-bottom: -9px;
    z-index: 1;
    content: "";
    margin-top: 167px;
}
.flow-steps .each-step span {
    position: relative;
    height: 15px;
    width: 15px;
    background-color: #e6992a;
    border-radius: 100%;
    display: flex;
    flex-wrap: wrap;
    margin: auto;
    z-index: 99;
    margin-top: 141px;
}

@media(max-width:768px) {
    .flow-steps .each-step .number {
        bottom: auto;
        left: 20%;
        margin-top: 130px;

    }
    .flow-steps .each-step .icon:after {
    transform: rotate(90deg);
    width: 180px;
    top: 101%;
    bottom: 0;
    margin: auto;
    display: flex;
    z-index: -1;
    margin-top: 138px;
}
}

.flow-steps .each-step .icon img {
	width: 130px
}

.flow-steps .each-step span {
    position: relative;
    height: 15px;
    width: 15px;
    background-color: #e6992a;
    border-radius: 100%;
    display: flex;
    flex-wrap: wrap;
    margin: auto;
    z-index: 99;
    margin-top: 140px;
}

    .flow-steps .each-step h4 {
    width: 100%;
    font-weight: 700;
    text-transform: uppercase;
    font-size: 18px;
    text-align: center;
    padding: 50px 10px 0;
    max-width: 70%;
    margin: auto;

}


@media(max-width:768px) {
	.flow-steps .each-step h4 {
		margin-bottom: 50px;
		background: #f3f3f3;
		z-index: 99;
		position: relative;
		padding: 0 10px
	}
}

.flow-steps .each-step .number {
	position: absolute;
	color: #888;
	opacity: .2;
	font-size: 110px;
	top: auto;
	bottom: 0;
	font-weight: 700;
	left: 10%;
	right: 80%;
	display: flex;
	flex-wrap: wrap;
	width: -webkit-fit-content;
	width: -moz-fit-content;
	width: fit-content;
	margin-bottom: 0;
	line-height: 76px
}

@media(max-width:620px) {
	.flow-steps .each-step .number {
		z-index: 100
	}
}

