.card {
	border: unset !important
}

.card .card-body {

	overflow: hidden;
	display: flex;
	padding: 0
}

.card .card-body a {
	border: none;
	overflow: hidden
}

.card .card-body a:hover {
	text-decoration: none
}

.card .card-body a .card-img {
	position: relative;
	height: 230px;
	transition: .8s ease-in-out
}

.card .card-body a .card-img img {
	-o-object-fit: cover;
	object-fit: cover;
	height: 100%;
	width: 100%;
	z-index: 0
}

@media screen and (max-width:757px) {
	.card .card-body a .card-img {
		height: auto
	}

	.card .card-body a .card-img img {
		-o-object-fit: unset;
		object-fit: unset
	}
}

.card .card-body .card-title {
	position: relative;
	background-color: #f3f3f3;
	width: 100%;
	min-height: 70px;
	display: flex;
	align-items: center;
	flex-wrap: wrap;
	justify-content: center;
	margin-bottom: 0;
	transition: .3s ease-in-out;
	z-index: 2
}

.card .card-body .card-title h4 {
    font-size: 20px;
    text-transform: uppercase;
    color: #161616;
    text-align: center;
    margin-bottom:8px;
    margin-top:0;
    font-weight:500;
}

.card .card-body .card-title:before {
	content: "";
	opacity: 0;
	transition: .3s ease-in-out
}

.card .card-body:hover .card-img:before {
	content: "";
	width: 100%;
	height: 100%;
	background-color: rgba(0, 0, 0, .3);
	position: absolute;
	transition: .3s ease-in-out
}

.card .card-body:hover .card-title {
	background-color: #e6992a;
	transition: .3s ease-in-out
}

.card .card-body:hover .card-title:before {
	content: "";
	position: absolute;
	top: -30px;
	width: 0;
	height: 0;
	border-left: 30px solid transparent;
	border-right: 30px solid transparent;
	border-bottom: 35px solid #e6992a;
	transform-origin: center bottom;
	transition: .3s ease-in-out;
	opacity: 1
}

.card .card-body:hover h4 {
	color: #fff
}

.listEquipment img{
    height: 100%;
    object-fit: cover;
}

