    function ShowHideDiv() {
        var input = document.getElementById("input");
        var inputdiv = document.getElementById("inputdiv");
        inputdiv.style.display = input.value == "Company" ? "block" : "none";


    }
$(document).ready(function() {
    $('form').on('submit', function(event) {

            select = $("#input").val();
            fname = $("#input-1").val();
            // company = $("#input-0").val();
            email = $("#input-3").val();
            mobile = $("#input-2").val();

            // if(select == "Company")
            // {
            //     if(company == "")
            //     {
            //         swal("error","Please add Company Name");
            //         return false;
            //     }
            // }

            if(select == "" || fname == "" || email == "" || mobile == "")
            {
                // alert('fill all details')
                swal("Error", "Please Enter All Compulsory Fields", "error");
                return false;
            }
            else if(!(/^[A-Za-z\s]*$/.test(fname)))
            {
                // alert( "Name must contain only characters and numbers" );
                swal("Error", "Please Enter Only Characters in Full Name", "error");
                return false;
            }
            else if(!(/^\d{10}$/.test(mobile)))
            {
                // alert( "num wrong" );
                swal("Error", "Please Enter Only 10 Digits in Mobile No", "error");
                return false;
            }

            else{

                frappe.call({
			    method: "validation", // write a function in api file
			    args:{
			        'email':email ,
                }
                }).then(records => {
                    var id = records["eid"]

                    if(id == email)
                    {
                        swal("Error", "Your Email Id Alrady Exists", "error");  

                    }
                    else
                    {
                		frappe.call({
                			method: "supplier_registration", // write a function in api file
                			args:{
                			    'suppliertype': $('#input').val(),
                			    'company':$('#input-0').val(),
                				'fname': $('#input-1').val(),
                				'mobile': $('#input-2').val(),
                				'email': $('#input-3').val(),
                				'wp':"",

                			},
                			callback: function(r) {
                			frappe.call({
                			method: "whatsapp_app.whatsapp_app.doctype.api.send_register_message", // write a function in api file
                			args:{
                				'name': $('#input-1').val(),
                				'number': $('#input-2').val(),
                				'types': 'supplier'
                			},
                			callback: function(r) {
                			    let data = r.message
                			    if(data[0] === true){
                			        frappe.call({
    			                        method: "whatsapp_app.whatsapp_app.doctype.api.contact_us_message",
    			                        args:{
                			                'mobile': $('#input-2').val(),
                			                'doctype': 'Supplier'
    			                        },callback: function(r) {
    			                            frappe.call({
    			                                method: "whatsapp_app.whatsapp_app.doctype.api.comment",
    			                                args:{
    			                                    'number': data[1],
    			                                    'template_name': data[2],
    			                                    'bt1': data[3]
    			                                }
    			                            })
    			                        }
    			                    })
                			    }
                				// frappe.msgprint("Thank you, we have received your requirement. Our team will get back to you shortly.");
                			    swal("Good job!", "Thank you, we have received your requirement. Our team will get back to you shortly.", "success");
                				$('#input').val('');
                				$('#input-0').val('');
                				$('#input-1').val('');
                				$('#input-2').val('');
                				$('#input-3').val('');
                			}
                		});
                			}
                		});
                    }
                });
            }
      event.preventDefault();
      });
});

