body{
    color: #212529;

}

.contact-us {
    padding: 50px 0;
    width: 100%;
}

/*left side div*/

.contact-us .left-side {
    height: 100%;
    padding: 35px 45px;
    align-content: flex-start;
}

.bg-primary {
    background-color: #e6992a!important;
}
.title-24.left-shape {
    padding-left: 40px;
    / height: -webkit-fit-content; /
    height: -moz-fit-content;
    height: fit-content;
}
.left-shape {
    position: relative;
}
.title-24 {
    margin-bottom: 35px;
}
.title-24 h5 {
    margin-bottom: 0;
    font-weight: 700;
}
.h3-title, .title-24 {
    text-transform: capitalize;
    font-weight: 700;
}

.contact-us .left-side .each-detail {
    height: -webkit-fit-content;
    height: -moz-fit-content;
    height: fit-content;
    border-bottom: 1px solid #161616;
    margin-bottom: 15px;
    width: 100%;
}
.contact-us .left-side .each-detail .wrapper {
    display: flex;
    flex-wrap: wrap;
}
.wrapper, img {
    max-width: 100%;
}
.wrapper {
    width: 1920px;
    margin: auto;
    display: block;
}
.contact-us .left-side .each-detail .wrapper .icon {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    align-self: flex-start;
    max-width: 40px;
}
.contact-us .left-side .each-detail .wrapper .icon svg {
    display: flex;
    flex-wrap: wrap;
    height: -webkit-fit-content;
    height: -moz-fit-content;
    height: fit-content;
}
img, svg {
    vertical-align: middle;
}
svg {
    overflow: hidden;
    fill:#161616;
}

.contact-us .left-side .each-detail .wrapper .details h4, .contact-us .left-side .each-detail .wrapper .details p {
    width: 100%;
    display: grid;
}
.h4, h4 {
    font-size: 1.5rem;
}
.right-side p {
    margin-bottom: 12px;
    font-size: 16px;
}
.contact-us .left-side .each-detail .wrapper .details {
    max-width: calc(100% - 40px);
    display: flex;
    flex-wrap: wrap;
    padding-left: 20px;
    width:372px;
}
.contact-us .left-side .each-detail .wrapper .details h4, .contact-us .left-side .each-detail .wrapper .details p {
    width: 100%;
    display: grid;
}
.contact-us .left-side .each-detail .wrapper .details p{
    color:#fff;
}
.contact-us .left-side .each-detail .wrapper .details h4
{
    margin-top:0;
}
.contact-us .left-side .each-detail .wrapper .details h4 a, .contact-us .left-side .each-detail .wrapper .details p a {
    color: #fff;
}

p {
    margin-top: 0;
    margin-bottom: 2rem;
}
body, p {
    font-size: 18px;
}

h4{
    margin-bottom: 0.5rem;
    font-weight: 500;
    line-height: 1.2;
}




/*contact form part*/
.contact-us .right-side {
        padding: 0px 35px;
}

.contact-shadow {
    box-shadow: 4px 0.5px 3rem rgb(0 0 0 / 15%);
}
.h3-title {
    margin-top: 1rem;
    font-size: 32px;
}


/*form*/


.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: 1px solid #ced4da;
    border-radius: 0.25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}


.contact-us .right-side .form-group {
    margin-bottom: 1rem;
}
.form-group .form-control {
    width: 100%!important;
    padding: 22px 15px!important;
    border-radius: 0!important;
}
.invalid-feedback {
    display: none;
    width: 100%;
    margin-top: 0.25rem;
    font-size: 80%;
    color: #dc3545;
}
fieldset {
    min-width: 0;
    padding: 0;
    margin: 0;
    border: 0;
}
.select .form-control{
    height:62px;
    margin-bottom:25px;
    /*height: calc(1.5em + 0.75rem + 2px);*/

}


.contact-us .right-side button {
    margin-top: 10px;
    margin-bottom: 10px;
}
.form-group #input-6 {
     width: 100%!important;
    padding-top: 23px!important;
    padding-bottom: 23px!important;
    /*padding-right: 114px!important;*/
    /*padding-left: 46px!important;*/
    / padding: 23px 88px!important; /
    border-radius: 0!important;
}

#textarea{
    border-radius:0;
    height:100px;
}
.iti{
    width:100%;
}

a{
    text-decoration:none;
}


.select .form-control {

    margin-bottom: 18px;
    height: calc(1.5em + 0.75rem + 2px);
}




/*bottom-shape*/
.left-shape {
  position: relative;
}
.left-shape::after, .left-shape::before {
  position: absolute;
  display: inline-block;
  width: 80%;
  background-color: #161616;
  height: 2px;
  top: 0;
  bottom: 0;
  margin-top: auto;
  margin-bottom: auto;
  left: 10px;
  margin-bottom: 14px;
  width: 20px;
  content: "";
}
.left-shape::after {
  margin-top: 15px;
  width: 30px;
  left: 0;
}

/*.bottom-shape {*/
/*  display: flex;*/
/*  margin: 20px 0 30px;*/
/*}*/

.bottom-shape {
    display: flex;
    margin: 0px 0 10px;
}

.bottom-shape.mt-10 {
  margin: 10px 0 30px;
}
.bottom-shape .left-shape {
  display: flex;
  flex-wrap: wrap;
  height: 23px;
  width: 40px;
}
.bottom-shape .left-shape:nth-child(2) {
  margin-left: 10px;
}
.bottom-shape .left-shape:nth-child(2)::before {
  left: 0;
}
.bottom-shape .left-shape::after {
  width: 40px;
  background-color: #e6992a;
}
.bottom-shape .left-shape::before {
  width: 20px;
  background-color: #e6992a;
  left: 20px;
}
::placeholder {
    font-size: 14px;
}



input::-webkit-outer-spin-button,
input::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}



.modal-title{
    color:red!important;
}

