$(document).ready(function() {
     $('form').on('submit', function(event) {
         select = $("#input").val()
            fname = $('#input-1').val();
            lname = $('#input-2').val();
            email = $('#input-5').val();
            mobile = $('#input-6').val();
         if(select=="" || fname=="" || lname=="" || email=="" || mobile=="") {
             swal("Error", "Please Enter All Compulsory Fields", "error");
            return false;

        }
        else if(!(/^[A-Za-z\s]*$/.test(fname)))
        {

                swal("Error", "Please Enter Only Characters In Name", "error");
                return false;
        }
        else if(!(/^[A-Za-z\s]*$/.test(lname)))
        {
                swal("Error", "Please Enter Only Characters In Name", "error");
                return false;
        }
        else if(!(/^\d{10}$/.test(mobile)))
        {
                swal("Error", "Please Enter Only 10 Digits In Mobile No", "error");
                return false;
        }
        else{

    			frappe.call({
    			method: "whatsapp_app.whatsapp_app.doctype.api.send_register_message", // write a function in api file
    			args:{
    				'name': $('#input-1').val(),
    				'number': $('#input-6').val(),
    				'types': 'inquiry',
    				'equipment_name': url.searchParams.get("title1")
    			},
                    callback: function(r){
                        let data = r.message
                        console.log("hello", data[0])
                	    if(data[0] === true){
                            frappe.call({
                    			method: "contact-us1", // write a function in api file
                    			args:{
                    			    'requesttype': $('#input').val(),
                    				'fname': $('#input-1').val(),
                    				'lname': $('#input-2').val(),
                    				'company': $('#input-3').val(),
                    				'equipment': $('#input-4').val(),
                    				'email': $('#input-5').val(),
                    				'mobile': $('#input-6').val(),
                    				'wp':$('#input-6').val(),
                    				'textarea': $('#textarea').val(),
                    				'nowdate':$('#nowdate').val(),
                    			},
                    			callback:function(r){
                    			     frappe.call({
    			                                method: "whatsapp_app.whatsapp_app.doctype.api.comment",
    			                                args:{
    			                                    'number': data[1],
    			                                    'template_name': data[2],
    			                                    'bt1': data[3]
    			                                }
    			                            });
                    			 //   alert("success with wp")

                    			}
                            });
                            swal("Good job!", "Thank you for contacting Migoo equipment. Our team is currently reviewing your inquiry, and we will get back to you soon.", "success");
                    			 setTimeout(function(){window.history.back();}, 3000);
                        }
                        else{
                             frappe.call({
                            			method: "contact-us1", // write a function in api file
                            			args:{
                            			    'requesttype': $('#input').val(),
                            				'fname': $('#input-1').val(),
                            				'lname': $('#input-2').val(),
                            				'company': $('#input-3').val(),
                            				'equipment': $('#input-4').val(),
                            				'email': $('#input-5').val(),
                            				'mobile': $('#input-6').val(),
                            				'textarea': $('#textarea').val(),
                            				'wp':" ",
                            				'nowdate':$('#nowdate').val(),
                            			},
                            			callback:function(r){
                            			 //   alert("success no wp")
                            			 swal("Good job!", "Thank you for contacting Migoo equipment. Our team is currently reviewing your inquiry, and we will get back to you soon.", "success");
                            			setTimeout(function(){window.history.back();}, 3000);

                            			}
                            });

                        }
                    }

            });
        }
      event.preventDefault();
      });
});




