}

/*left side div*/

.contact-us .left-side {
    height: 100%;
    padding: 35px 45px;
    align-content: flex-start;
}

.bg-primary {
    background-color: #e6992a!important;
}
.title-24.left-shape {
    padding-left: 40px;
    / height: -webkit-fit-content; /
