}
.title-24 {
    margin-bottom: 35px;
}
.title-24 h5 {
    margin-bottom: 0;
    font-weight: 700;
}
.h3-title, .title-24 {
    text-transform: capitalize;
    font-weight: 700;
}

.contact-us .left-side .each-detail {
    height: -webkit-fit-content;
    height: -moz-fit-content;
    height: fit-content;
    border-bottom: 1px solid #161616;
    margin-bottom: 15px;
    width: 100%;
}
.contact-us .left-side .each-detail .wrapper {
    display: flex;
    flex-wrap: wrap;
}
.wrapper, img {
    max-width: 100%;
}
.wrapper {
    width: 1920px;
    margin: auto;
    display: block;
}
.contact-us .left-side .each-detail .wrapper .icon {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    align-self: flex-start;
    max-width: 40px;
}
.contact-us .left-side .each-detail .wrapper .icon svg {
    display: flex;
    flex-wrap: wrap;
    height: -webkit-fit-content;
    height: -moz-fit-content;
    height: fit-content;
}
img, svg {
    vertical-align: middle;
}
svg {
    overflow: hidden;
    fill:#161616;
}

.contact-us .left-side .each-detail .wrapper .details h4, .contact-us .left-side .each-detail .wrapper .details p {
    width: 100%;
    display: grid;
